import React from "react";
import useFetch from "./FetchDating";
import { PieChart, Pie, Label, Cell, ResponsiveContainer } from "recharts";

const RadialScore = () => {
  const [loading, dataScore] = useFetch("http://localhost:3000/user/12");

  if (loading) {
    <div>Chargement...</div>;
  }

  //console.log(dataScore.data.todayScore*100)

  // const data = [{
  //   score: dataScore.data.todayScore*100 }]

  const data = [
    {
      score: 12,
    },
  ];

  return (
    // <ResponsiveContainer width="100%" height="80%">
      <PieChart width={250} height={180}>
        <Pie
          data={data}
          dataKey="score"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={50}
          fill="#FBFBFB"
        />
        <Pie
          data={data}
          dataKey="score"
          nameKey="name"
          cx="50%"
          cy="50%"
          innerRadius={60}
          outerRadius={80}
          fill="#FF0000"
          label
        />
      </PieChart>
    // </ResponsiveContainer>
  );
};

export default RadialScore;
