import React from "react";
import {
  LineChart,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  Line,
  Bar,
  ResponsiveContainer,
  Area,
} from "recharts";
import useFetch from "./FetchDating";
import { useLocation, useParams } from "react-router-dom";
import TooltipsAverage from "./TooltipsAverage";

const sessions= [
    {
        day: 1,
        sessionLength: 30
    },
    {
        day: 2,
        sessionLength: 23
    },
    {
        day: 3,
        sessionLength: 45
    },
    {
        day: 4,
        sessionLength: 50
    },
    {
        day: 5,
        sessionLength: 0
    },
    {
        day: 6,
        sessionLength: 0
    },
    {
        day: 7,
        sessionLength: 60
    }
]

const AverageSessions = () => {
const {id} = useParams()    
const [loading, averageData]= useFetch(`http://localhost:3000/user/${id}/average-sessions`)


const dataOfDay= sessions.map(x=>x.day)
console.log(dataOfDay)

const transformAxisX= ()=>{
   for (let day in dataOfDay){


 let day = day;
 switch (day) {
      case 1:
        day = "L";
        break;
      case 2:
       day = "M";
        break;
      case 3:
        day = "M";
        break;
      case 4:
        day = "J";
        break;
      case 5:
        day = "V";
        break;
      case 6:
        day = "S";
        break;
      case 7:
        day = "D";
        break;
      default:
        day = "";
    }

    return day; 
}
  }



  if (loading){
    return <div>Chargement...</div>
}

  return (

   <div className='average_container'>
    <h2>DurÃ©e moyenne des sessions</h2>
    <ResponsiveContainer width="100%" height="80%">
<LineChart width={258} height={263} data={averageData.data.sessions}
  margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
  <CartesianGrid horizontal= {false} vertical={false} strokeDasharray="0" />
  <XAxis
                dataKey={transformAxisX}
                stroke="rgba(255, 255, 255, 0.5)"
                tickLine={false}
                axisLine={false}
                tick={{ fontSize: 12 }}
              />
  <YAxis hide='true' />
  <Tooltip content={<TooltipsAverage/>}  cursor={{ fill: "#FFFFFF" }}/>
 <Line type="monotone"
                dataKey="sessionLength"
                stroke="#fff"
                strokeWidth={2}
                dot={false}
                activeDot={{
                  stroke: "rgba(255, 255, 255, 0.2)",
                  strokeWidth: 10,
                  r: 5,
                }}/>
  {/* <Line hide='true' type="monotone" dataKey="day" stroke="#82ca9d" /> */}
</LineChart>
</ResponsiveContainer>
  </div>
)
};

export default AverageSessions;
