import React from 'react';
import {PolarRadiusAxis,PolarGrid, PolarAngleAxis, Radar, Legend, RadarChart,ResponsiveContainer} from 'recharts'
import useFetch from './FetchDating';


const dataTest= [


            {
                value: 80,
                kind: 1
            },
            {
                value: 120,
                kind: 2
            },
            {
                value: 140,
                kind: 3
            },
            {
                value: 50,
                kind: 4
            },
            {
                value: 200,
                kind: 5
            },
            {
                value: 90,
                kind: 6
            }
        ]





const RadarPerformance = () => {

const [loading, dataPerformance]= useFetch('http://localhost:3000/user/12/performance')

if (loading){
    <div>Chargement...</div>
}



    return (
        <div className='radar_performance_container'>
  <RadarChart outerRadius={90} width={190} height={250} data={dataTest}>
  <PolarGrid />
  <PolarAngleAxis  dataKey='kind' fill='#FFFFFF' />
  <PolarRadiusAxis angle={30} allowDuplicatedCategory/>
  <Radar dataKey="value" stroke="transparent" fill="#FF0101B2" fillOpacity={0.7} />


</RadarChart>
        </div>
    );
};

export default RadarPerformance;
