import useFetch from "./FetchDating";
import { PieChart, Pie, Label, Cell, ResponsiveContainer } from "recharts";

const RadialScore = () => {
  const [loading, dataScore] = useFetch("http://localhost:3000/user/12");

  if (loading) {
    <div>Chargement...</div>;
  }

  //console.log(dataScore.data.todayScore*100)

  // const data = [{
  //   score: dataScore.data.todayScore*100 }]

  const data = [
    {
      score: 12,
    },
  ];

  return (
    <div className='radar_score_container'>  
    <h2>Score</h2>
    <ResponsiveContainer width='100%' height='100%'>
    <PieChart width={250} height={250}>

  <Pie data={data} dataKey="score" nameKey="name" cx="50%" cy="50%" innerRadius={70} outerRadius={80} fill="#FF0000" 
 label />
</PieChart>



    </ResponsiveContainer>


    </div>
  );
};

export default RadialScore;
